exports.get = (commande) => {
	//séparation paramètre
	let data = commande.split(' ');
	//exclusion de l'entête
	let entete = data.shift();
	//traitement
	let resultat = [];
	for(let ele of data) {
		if( ele.match( /^[0-9]+$/ ) ) {
			resultat.push ( '1d' + ele + ': ' + de(parseInt(ele)));
		} else {
			let temp = ele.split('d');
			let nombreDeDe = parseInt(temp[0]);
			let nombreDeFace = parseInt(temp[1]);
			let tirage = Array.from(new Array(nombreDeDe),k=>de(nombreDeFace));
			resultat.push( ele + ': ' + tirage.join('; ') );
		}
	}
	let Message = 'Le résultat de votre tirage est : ';
	return Message + '\n' + resultat.join('\n');
};

const de = face => Math.ceil( Math.random() * face );
