exports.print = (element) => {
	if(typeof element != 'string' )
		element = element.toString();
	
	let liste = element.split('\n');
	let maxLength = Math.max(...(liste.map(k=>k.length)));
	
	console.log('╔' + '═'.repeat(maxLength+2) + '╗');
	for(let line of liste)
		console.log('║ ' + line + ' '.repeat(maxLength-line.length) + ' ║');
	console.log('╚' + '═'.repeat(maxLength+2) + '╝');
};